import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  Alert,
  Modal,
  TouchableOpacity,
  Image,
  ActivityIndicator,
  StyleSheet,
  Platform
} from 'react-native';

const API_URL = 'http://localhost:8000/tasks';

const StudentDashboard = ({ token, username }) => {
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({ completed: 0, pending: 0, progress: 0 });
  const [loading, setLoading] = useState(true);
  const [selectedTask, setSelectedTask] = useState(null);
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (loading) {
        setLoading(false);
        Alert.alert('Timeout', 'La carga est치 tardando demasiado. Verifica la conexi칩n.');
      }
    }, 15000);

    testConnection();

    return () => clearTimeout(timeoutId);
  }, [token]);

  const testConnection = async () => {
    const endpoints = [
      '/tasks',
    ];

    for (const endpoint of endpoints) {
      try {
        console.log(`Testing endpoint: ${API_URL}${endpoint}`);
        setDebugInfo(`Probando: ${endpoint}`);
        
        const response = await fetch(`${API_URL}${endpoint}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 5000
        });

        console.log(`${endpoint} - Status:`, response.status);

        if (response.ok) {
          const data = await response.json();
          console.log(`${endpoint} - Data:`, data);
          
          setTasks(Array.isArray(data) ? data : []);
          const completed = (Array.isArray(data) ? data : []).filter(t => t.completed).length;
          const total = (Array.isArray(data) ? data : []).length;
          
          setStats({
            completed,
            pending: total - completed,
            progress: total > 0 ? Math.round((completed / total) * 100) : 0
          });
          
          setDebugInfo(`Conectado exitosamente a: ${endpoint}`);
          setLoading(false);
          return; // Exit on first successful connection
        }
      } catch (error) {
        console.log(`${endpoint} failed:`, error.message);
      }
    }

    // Si ning칰n endpoint funciona
    setDebugInfo('No se pudo conectar a ning칰n endpoint');
    Alert.alert(
      'Error de Conexi칩n', 
      `No se pudo conectar al servidor en ${API_URL}\n\n` +
      'Endpoints probados:\n' + endpoints.join('\n') + '\n\n' +
      '쮼st치 el servidor corriendo?'
    );
    setTasks([]);
    setStats({ completed: 0, pending: 0, progress: 0 });
    setLoading(false);
  };

  const toggleTaskCompletion = async (taskId, currentStatus) => {
    const endpoint = currentStatus ? 'uncomplete' : 'complete';
    try {
      const response = await fetch(`${API_URL}/tasks/${taskId}/${endpoint}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        testConnection(); // Refresh data
      } else {
        throw new Error(`Error: ${response.status}`);
      }
    } catch (error) {
      console.error('Error updating task:', error);
      Alert.alert('Error', 'No se pudo actualizar la tarea');
    }
  };

  if (loading) {
    return (
      <View style={styles.centerContainer}>
        <ActivityIndicator size="large" color="#7c3aed" />
        <Text style={styles.loadingText}>Cargando tareas...</Text>
        <Text style={styles.debugText}>{debugInfo}</Text>
        <TouchableOpacity 
          style={styles.retryButton} 
          onPress={() => {
            setLoading(true);
            testConnection();
          }}
        >
          <Text style={styles.retryButtonText}>Reintentar Conexi칩n</Text>
        </TouchableOpacity>
        <TouchableOpacity 
          style={styles.skipButton} 
          onPress={() => {
            setLoading(false);
            setTasks([]);
          }}
        >
          <Text style={styles.skipButtonText}>Continuar Sin Datos</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>游뱄 Cantera de Empresas</Text>
        <Text style={styles.welcomeText}>Bienvenido, {username || 'Usuario'}</Text>
        {debugInfo && <Text style={styles.debugInfo}>{debugInfo}</Text>}
      </View>

      <View style={styles.statsContainer}>
        <View style={styles.statCard}>
          <Text style={styles.statLabel}>Tareas Completadas</Text>
          <Text style={styles.statValue}>{stats.completed}</Text>
        </View>
        <View style={styles.statCard}>
          <Text style={styles.statLabel}>Tareas Pendientes</Text>
          <Text style={[styles.statValue, { color: '#f97316' }]}>{stats.pending}</Text>
        </View>
        <View style={styles.statCard}>
          <Text style={styles.statLabel}>Progreso Total</Text>
          <Text style={[styles.statValue, { color: '#7c3aed' }]}>{stats.progress}%</Text>
        </View>
      </View>

      <View style={styles.tasksSection}>
        <TouchableOpacity 
          style={styles.refreshButton} 
          onPress={() => {
            setLoading(true);
            testConnection();
          }}
        >
          <Text style={styles.refreshButtonText}>游댃 Actualizar</Text>
        </TouchableOpacity>

        {tasks.length === 0 ? (
          <View style={styles.emptyState}>
            <Text style={styles.emptyText}>No hay tareas disponibles</Text>
            <Text style={styles.emptySubtext}>
              {debugInfo || 'Verifica tu conexi칩n al servidor'}
            </Text>
          </View>
        ) : (
          tasks.map((item, index) => (
            <TouchableOpacity 
              key={item.submission_id || item.id || index} 
              style={styles.taskCard} 
              onPress={() => setSelectedTask(item)}
            >
              {item.task?.image_path && (
                <Image 
                  source={{ uri: `${API_URL}${item.task.image_path}` }} 
                  style={styles.taskImage}
                  onError={() => console.log('Error loading image')}
                />
              )}
              <View style={styles.taskInfo}>
                <Text style={styles.taskTitle}>{item.task?.title || item.title || 'Sin t칤tulo'}</Text>
                <Text style={styles.taskDescription}>{item.task?.description || item.description || 'Sin descripci칩n'}</Text>
                <TouchableOpacity 
                  style={[styles.taskButton, item.completed && styles.completedButton]} 
                  onPress={() => toggleTaskCompletion(item.task?.id || item.id, item.completed)}
                >
                  <Text style={styles.taskButtonText}>
                    {item.completed ? 'Marcar como Pendiente' : 'Marcar como Completada'}
                  </Text>
                </TouchableOpacity>
              </View>
            </TouchableOpacity>
          ))
        )}
      </View>

      <Modal visible={selectedTask !== null} animationType="slide" transparent={true} onRequestClose={() => setSelectedTask(null)}>
        {selectedTask && (
          <View style={styles.modalOverlay}>
            <View style={styles.modalContent}>
              <Text style={styles.modalTitle}>{selectedTask.task?.title || selectedTask.title || 'Sin t칤tulo'}</Text>
              {selectedTask.task?.image_path && (
                <Image 
                  source={{ uri: `${API_URL}${selectedTask.task.image_path}` }} 
                  style={styles.modalImage}
                  onError={() => console.log('Error loading modal image')}
                />
              )}
              <Text style={styles.modalDescription}>{selectedTask.task?.description || selectedTask.description || 'Sin descripci칩n'}</Text>
              <TouchableOpacity 
                style={[styles.modalButton, selectedTask.completed && styles.completedButton]} 
                onPress={() => {
                  toggleTaskCompletion(selectedTask.task?.id || selectedTask.id, selectedTask.completed);
                  setSelectedTask(null);
                }}
              >
                <Text style={styles.taskButtonText}>
                  {selectedTask.completed ? 'Marcar como Pendiente' : 'Marcar como Completada'}
                </Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={styles.closeButton} 
                onPress={() => setSelectedTask(null)}
              >
                <Text style={styles.closeButtonText}>Cerrar</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}
      </Modal>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
