import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  Alert,
  Modal,
  TouchableOpacity,
  Image,
  ActivityIndicator,
  StyleSheet,
  Platform
} from 'react-native';

const API_URL = 'http://localhost:8000';

const StudentDashboard = ({ token, username }) => {
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({ completed: 0, pending: 0, progress: 0 });
  const [loading, setLoading] = useState(true);
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`${API_URL}/my-tasks`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      setTasks(data);
      const completed = data.filter(t => t.completed).length;
      const total = data.length;
      setStats({
        completed,
        pending: total - completed,
        progress: total > 0 ? Math.round((completed / total) * 100) : 0
      });
    } catch (error) {
      Alert.alert('Error', 'No se pudieron cargar las tareas');
    } finally {
      setLoading(false);
    }
  };

  const toggleTaskCompletion = async (taskId, currentStatus) => {
    const endpoint = currentStatus ? 'uncomplete' : 'complete';
    try {
      const response = await fetch(`${API_URL}/tasks/${taskId}/${endpoint}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) fetchTasks();
    } catch (error) {
      Alert.alert('Error', 'No se pudo actualizar la tarea');
    }
  };

  if (loading) {
    return (
      <View style={styles.centerContainer}>
        <ActivityIndicator size="large" color="#7c3aed" />
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>🤖 Cantera de Empresas</Text>
        <Text style={styles.welcomeText}>Bienvenido, {username}</Text>
      </View>

      <View style={styles.statsContainer}>
        <View style={styles.statCard}><Text style={styles.statLabel}>Tareas Completadas</Text><Text style={styles.statValue}>{stats.completed}</Text></View>
        <View style={styles.statCard}><Text style={styles.statLabel}>Tareas Pendientes</Text><Text style={[styles.statValue, { color: '#f97316' }]}>{stats.pending}</Text></View>
        <View style={styles.statCard}><Text style={styles.statLabel}>Progreso Total</Text><Text style={[styles.statValue, { color: '#7c3aed' }]}>{stats.progress}%</Text></View>
      </View>

      <View style={styles.tasksSection}>
        {tasks.map(item => (
          <TouchableOpacity key={item.submission_id} style={styles.taskCard} onPress={() => setSelectedTask(item)}>
            <Image source={{ uri: `${API_URL}${item.task.image_path}` }} style={styles.taskImage} />
            <View style={styles.taskInfo}>
              <Text style={styles.taskTitle}>{item.task.title}</Text>
              <Text style={styles.taskDescription}>{item.task.description}</Text>
              <TouchableOpacity style={[styles.taskButton, item.completed && styles.completedButton]} onPress={() => toggleTaskCompletion(item.task.id, item.completed)}>
                <Text style={styles.taskButtonText}>{item.completed ? 'Marcar como Pendiente' : 'Marcar como Completada'}</Text>
              </TouchableOpacity>
            </View>
          </TouchableOpacity>
        ))}
      </View>

      <Modal visible={selectedTask !== null} animationType="slide" transparent={true} onRequestClose={() => setSelectedTask(null)}>
        {selectedTask && (
          <View style={styles.modalOverlay}>
            <View style={styles.modalContent}>
              <Text style={styles.modalTitle}>{selectedTask.task.title}</Text>
              <Image source={{ uri: `${API_URL}${selectedTask.task.image_path}` }} style={styles.modalImage} />
              <Text style={styles.modalDescription}>{selectedTask.task.description}</Text>
              <TouchableOpacity style={[styles.modalButton, selectedTask.completed && styles.completedButton]} onPress={() => {
                toggleTaskCompletion(selectedTask.task.id, selectedTask.completed);
                setSelectedTask(null);
              }}>
                <Text style={styles.taskButtonText}>{selectedTask.completed ? 'Marcar como Pendiente' : 'Marcar como Completada'}</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}
      </Modal>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f3f4f6' },
  centerContainer: { flex: 1, justifyContent: 'center', alignItems: 'center' },
  header: { backgroundColor: 'white', padding: 20, paddingTop: Platform.OS === 'ios' ? 50 : 20 },
  headerTitle: { fontSize: 24, fontWeight: 'bold', color: '#7c3aed' },
  welcomeText: { fontSize: 16, color: '#6b7280', marginTop: 5 },
  statsContainer: { padding: 20, gap: 15 },
  statCard: { backgroundColor: 'white', padding: 20, borderRadius: 10, marginBottom: 15, elevation: 3 },
  statLabel: { fontSize: 14, color: '#6b7280' },
  statValue: { fontSize: 32, fontWeight: 'bold', color: '#10b981' },
  tasksSection: { padding: 20 },
  taskCard: { backgroundColor: 'white', borderRadius: 10, marginBottom: 15, elevation: 3, overflow: 'hidden' },
  taskImage: { width: '100%', height: 200 },
  taskInfo: { padding: 15 },
  taskTitle: { fontSize: 18, fontWeight: 'bold', marginBottom: 5 },
  taskDescription: { fontSize: 14, color: '#6b7280', marginBottom: 10 },
  taskButton: { backgroundColor: '#7c3aed', padding: 12, borderRadius: 8, alignItems: 'center' },
  completedButton: { backgroundColor: '#10b981' },
  taskButtonText: { color: 'white', fontWeight: 'bold' },
  modalOverlay: { flex: 1, backgroundColor: 'rgba(0, 0, 0, 0.5)', justifyContent: 'center', alignItems: 'center' },
  modalContent: { backgroundColor: 'white', borderRadius: 10, padding: 20, width: '90%', maxWidth: 500 },
  modalTitle: { fontSize: 24, fontWeight: 'bold', marginBottom: 15 },
  modalImage: { width: '100%', height: 300, marginBottom: 15 },
  modalDescription: { fontSize: 16, color: '#4b5563', marginBottom: 20, lineHeight: 24 },
  modalButton: { backgroundColor: '#7c3aed', padding: 15, borderRadius: 8, alignItems: 'center' }
});

export default StudentDashboard;
