import React, { useState } from 'react';
import {
  View, Text, TextInput, TouchableOpacity, ActivityIndicator, Alert, StyleSheet
} from 'react-native';
import { login } from './auth';
import AsyncStorage from '@react-native-async-storage/async-storage';

const LoginScreen = ({ setToken, setRole, setUsername, setIsAuthenticated }) => {
  const [usernameInput, setUsernameInput] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    if (!usernameInput || !password) {
      Alert.alert('Error', 'Por favor ingresa usuario y contrase√±a');
      return;
    }

    setLoading(true);
    try {
      const data = await login(usernameInput, password);

      // Guardar tambi√©n el username
      await AsyncStorage.setItem('username', usernameInput);

      // Verifica si el token y rol est√°n en AsyncStorage
      const storedToken = await AsyncStorage.getItem('access_token');
      const storedRole = await AsyncStorage.getItem('user_role');

      if (!storedToken || !storedRole) {
        Alert.alert('Error', 'No se pudo guardar el token o rol');
        return;
      }

      setToken(storedToken);
      setRole(storedRole);
      setUsername(usernameInput);
      setIsAuthenticated(true);
    } catch (error) {
      Alert.alert('Error', error.message || 'Error de conexi√≥n');
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.loginContainer}>
      <View style={styles.loginCard}>
        <Text style={styles.loginTitle}>ü§ñ Cantera de Empresas</Text>
        <TextInput
          style={styles.input}
          placeholder="Usuario"
          value={usernameInput}
          onChangeText={setUsernameInput}
          autoCapitalize="none"
        />
        <TextInput
          style={styles.input}
          placeholder="Contrase√±a"
          value={password}
          onChangeText={setPassword}
          secureTextEntry
        />
        <TouchableOpacity
          style={styles.loginButton}
          onPress={handleLogin}
          disabled={loading}
        >
          {loading ? (
            <ActivityIndicator color="white" />
          ) : (
            <Text style={styles.loginButtonText}>Iniciar Sesi√≥n</Text>
          )}
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  loginContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f3f4f6',
  },
  loginCard: {
    backgroundColor: 'white',
    padding: 30,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 5,
    width: '90%',
    maxWidth: 400,
  },
  loginTitle: {
    fontSize: 28,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 30,
    color: '#7c3aed',
  },
  input: {
    borderWidth: 1,
    borderColor: '#e5e7eb',
    borderRadius: 8,
    padding: 12,
    marginBottom: 15,
    fontSize: 16,
  },
  loginButton: {
    backgroundColor: '#7c3aed',
    padding: 15,
    borderRadius: 8,
    alignItems: 'center',
    marginTop: 20,
  },
  loginButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default LoginScreen;
