import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  Alert,
  Modal,
  TouchableOpacity,
  Image,
  ActivityIndicator,
  StyleSheet,
  Platform
} from 'react-native';

const API_URL = 'http://localhost:8000';

const StudentDashboard = ({ username }) => {
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({ completed: 0, pending: 0, progress: 0 });
  const [loading, setLoading] = useState(true);
  const [selectedTask, setSelectedTask] = useState(null);
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (loading) {
        setLoading(false);
        Alert.alert('Timeout', 'La carga est谩 tardando demasiado. Verifica la conexi贸n.');
      }
    }, 15000);

    fetchTasks();

    return () => clearTimeout(timeoutId);
  }, []);

  const fetchTasks = async () => {
    try {
      setDebugInfo('Cargando tareas generales...');
      // Asumiendo que no es necesario enviar token porque las tareas son p煤blicas
      const response = await fetch(`${API_URL}/tasks`);

      console.log('/tasks - Status:', response.status);

      if (response.ok) {
        const data = await response.json();
        console.log('/tasks - Data:', data);

        // En este caso no hay "completadas" ni "pendientes" individuales,
        // as铆 que mostramos todas como pendientes (o adaptas seg煤n tu l贸gica)
        setTasks(Array.isArray(data) ? data : []);
        const total = Array.isArray(data) ? data.length : 0;

        setStats({
          completed: 0, // No trackeamos completadas porque son generales
          pending: total,
          progress: 0,
        });

        setDebugInfo('Tareas generales cargadas correctamente');
        setLoading(false);
      } else {
        throw new Error(`Status ${response.status}`);
      }
    } catch (error) {
      console.log('Error fetchTasks:', error.message);
      setDebugInfo('Error al cargar tareas');
      Alert.alert('Error', 'No se pudieron cargar las tareas generales.');
      setLoading(false);
      setTasks([]);
      setStats({ completed: 0, pending: 0, progress: 0 });
    }
  };

  if (loading) {
    return (
      <View style={styles.centerContainer}>
        <ActivityIndicator size="large" color="#7c3aed" />
        <Text style={styles.loadingText}>Cargando tareas...</Text>
        <Text style={styles.debugText}>{debugInfo}</Text>
        <TouchableOpacity 
          style={styles.retryButton} 
          onPress={() => {
            setLoading(true);
            fetchTasks();
          }}
        >
          <Text style={styles.retryButtonText}>Reintentar Carga</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}> Cantera de Empresas</Text>
        <Text style={styles.welcomeText}>Bienvenido, {username || 'Usuario'}</Text>
        {debugInfo && <Text style={styles.debugInfo}>{debugInfo}</Text>}
      </View>

      <View style={styles.statsContainer}>
        <View style={styles.statCard}>
          <Text style={styles.statLabel}>Tareas Disponibles</Text>
          <Text style={[styles.statValue, { color: '#f97316' }]}>{stats.pending}</Text>
        </View>
      </View>

      <View style={styles.tasksSection}>
        <TouchableOpacity 
          style={styles.refreshButton} 
          onPress={() => {
            setLoading(true);
            fetchTasks();
          }}
        >
          <Text style={styles.refreshButtonText}> Actualizar</Text>
        </TouchableOpacity>

        {tasks.length === 0 ? (
          <View style={styles.emptyState}>
            <Text style={styles.emptyText}>No hay tareas disponibles</Text>
            <Text style={styles.emptySubtext}>{debugInfo || 'Verifica tu conexi贸n al servidor'}</Text>
          </View>
        ) : (
          tasks.map((item, index) => (
            <TouchableOpacity 
              key={item.id || index} 
              style={styles.taskCard} 
              onPress={() => setSelectedTask(item)}
            >
              {item.image_path && (
                <Image 
                  source={{ uri: `${API_URL}${item.image_path}` }} 
                  style={styles.taskImage}
                  onError={() => console.log('Error loading image')}
                />
              )}
              <View style={styles.taskInfo}>
                <Text style={styles.taskTitle}>{item.title || 'Sin t铆tulo'}</Text>
                <Text style={styles.taskDescription}>{item.description || 'Sin descripci贸n'}</Text>
              </View>
            </TouchableOpacity>
          ))
        )}
      </View>

      <Modal visible={selectedTask !== null} animationType="slide" transparent={true} onRequestClose={() => setSelectedTask(null)}>
        {selectedTask && (
          <View style={styles.modalOverlay}>
            <View style={styles.modalContent}>
              <Text style={styles.modalTitle}>{selectedTask.title || 'Sin t铆tulo'}</Text>
              {selectedTask.image_path && (
                <Image 
                  source={{ uri: `${API_URL}${selectedTask.image_path}` }} 
                  style={styles.modalImage}
                  onError={() => console.log('Error loading modal image')}
                />
              )}
              <Text style={styles.modalDescription}>{selectedTask.description || 'Sin descripci贸n'}</Text>
              <TouchableOpacity 
                style={styles.closeButton} 
                onPress={() => setSelectedTask(null)}
              >
                <Text style={styles.closeButtonText}>Cerrar</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}
      </Modal>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f3f4f6' },
  centerContainer: { flex: 1, justifyContent: 'center', alignItems: 'center', padding: 20 },
  loadingText: { marginTop: 10, fontSize: 16, color: '#6b7280', textAlign: 'center' },
  debugText: { marginTop: 5, fontSize: 12, color: '#9ca3af', textAlign: 'center' },
  debugInfo: { fontSize: 12, color: '#9ca3af', marginTop: 5 },
  header: { backgroundColor: 'white', padding: 20, paddingTop: Platform.OS === 'ios' ? 50 : 20 },
  headerTitle: { fontSize: 24, fontWeight: 'bold', color: '#7c3aed' },
  welcomeText: { fontSize: 16, color: '#6b7280', marginTop: 5 },
  statsContainer: { padding: 20, gap: 15 },
  statCard: { backgroundColor: 'white', padding: 20, borderRadius: 10, marginBottom: 15, elevation: 3 },
  statLabel: { fontSize: 14, color: '#6b7280' },
  statValue: { fontSize: 32, fontWeight: 'bold', color: '#f97316' },
  tasksSection: { padding: 20 },
  refreshButton: { backgroundColor: '#7c3aed', padding: 10, borderRadius: 8, alignItems: 'center', marginBottom: 15 },
  refreshButtonText: { color: 'white', fontWeight: 'bold' },
  emptyState: { backgroundColor: 'white', padding: 40, borderRadius: 10, alignItems: 'center' },
  emptyText: { fontSize: 16, color: '#6b7280', marginBottom: 5 },
  emptySubtext: { fontSize: 12, color: '#9ca3af', textAlign: 'center' },
  taskCard: { backgroundColor: 'white', borderRadius: 10, marginBottom: 15, elevation: 3, overflow: 'hidden' },
  taskImage: { width: '100%', height: 200 },
  taskInfo: { padding: 15 },
  taskTitle: { fontSize: 18, fontWeight: 'bold', marginBottom: 5 },
  taskDescription: { fontSize: 14, color: '#6b7280', marginBottom: 10 },
  modalOverlay: { flex: 1, backgroundColor: 'rgba(0, 0, 0, 0.5)', justifyContent: 'center', alignItems: 'center' },
  modalContent: { backgroundColor: 'white', borderRadius: 10, padding: 20, width: '90%', maxWidth: 500 },
  modalTitle: { fontSize: 24, fontWeight: 'bold', marginBottom: 15 },
  modalImage: { width: '100%', height: 300, marginBottom: 15 },
  modalDescription: { fontSize: 16, color: '#4b5563', marginBottom: 20, lineHeight: 24 },
  closeButton: { backgroundColor: '#e5e7eb', padding: 15, borderRadius: 8, alignItems: 'center' },
  closeButtonText: { color: '#6b7280', fontWeight: 'bold' },
  retryButton: { backgroundColor: '#7c3aed', paddingHorizontal: 20, paddingVertical: 10, borderRadius: 8, marginTop: 15 },
  retryButtonText: { color: 'white', fontWeight: 'bold' },
});

export default StudentDashboard;
